var React = require('react');
var Renderer = require('../components/renderer.js');

var Other = React.createClass({
    render: function () {
        return (
            <div>
                <h1>Random Route ID: {this.props.id}</h1>
                <p>Go ahead and click another. You know you want to.</p>
                <a href={this.props.random}>/{this.props.random}</a>
                <p>Or just go back to the index if you want...I guess</p>
                <a href="/">index</a>
                <Renderer renderer={this.props.renderer} />
            </div>
        );
    }
});

module.exports = Other;