module.exports = {
    '/': function (renderAndSend) {
        renderAndSend(null,
            require('app/react/js/views/index'),
            {
                'random': Math.ceil(Math.random() * 10000000)
            }
        );
    },
    '/:id': function (id, renderAndSend) {
        renderAndSend(null,
            require('app/react/js/views/other'),
            {
                'id': id,
                'random': Math.ceil(Math.random() * 10000000)
            }
        );
    }
};